INSERT INTO `wp_posts` VALUES (117, 1, '2018-11-03 06:12:07', '2018-11-03 06:12:07', '<h3 style="text-align: center;">BALAVICA</h3>\r\n<h4 style="text-align: center;">Balavica, school for special children – is established as a social initiative to serve the children in need with all kinds of developmental disabilities such as ASD (autistic spectrum disorder), CP, MR, Down Syndrome. The school has started inJan 2018 with a goal to provide quality education, structured training and therapy to the children to meet their individual needs, preparing them for a normal life etc. Currently, we have 9 full time children and 4 full-time staffs to assist them</h4>', 'Balavica', '', 'inherit', 'closed', 'closed', '', '91-revision-v1', '', '', '2018-11-03 06:12:07', '2018-11-03 06:12:07', '', 91, 'http://tnfct.org/2018/11/03/91-revision-v1/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES (118, 1, '2018-11-03 06:22:21', '2018-11-03 06:22:21', '<h4>WHAT WE DO\nOn behalf of TNFCT, we are performing various social activities which include:</h4>\n<ul>\n 	<li style="text-align: center;">\n<h4 style="text-align: left;">plantation on road side with proper local body permission</h4>\n</li>\n 	<li style="text-align: left;">\n<h4>provide essential things to local government schools</h4>\n</li>\n 	<li style="text-align: left;">\n<h4>distribute meals to kids in local orphanage on festival occasions</h4>\n</li>\n 	<li style="text-align: center;">\n<h4 style="text-align: left;">provide financial aid for financially deprived children--to enable them to resume their education without any hindrance.</h4>\n</li>\n</ul>\n<h4>Besides these, our Trust''s primary focus is "Balavica"--a school for special children</h4>\n<ul>\n 	<li>\n<h4>to serve these "gifted" children depending on their need in order to enable them lead a normal life</h4>\n</li>\n</ul>', 'What We Do', '', 'inherit', 'closed', 'closed', '', '89-autosave-v1', '', '', '2018-11-03 06:22:21', '2018-11-03 06:22:21', '', 89, 'http://tnfct.org/2018/11/03/89-autosave-v1/', 0, 'revision', '', 0);
