INSERT INTO `wp_posts` VALUES (125, 1, '2018-11-24 22:51:44', '2018-11-24 22:51:44', '<div id="pl-91"  class="panel-layout" ><div id="pg-91-0"  class="panel-grid panel-no-style" ><div id="pgc-91-0-0"  class="panel-grid-cell"  data-weight="1" ><div id="panel-91-0-0-0" class="so-panel widget widget_sow-editor panel-first-child" data-index="0" data-style="{&quot;background_image_attachment&quot;:false,&quot;background_display&quot;:&quot;tile&quot;}" ><div class="so-widget-sow-editor so-widget-sow-editor-base">\n<div class="siteorigin-widget-tinymce textwidget">\n	<h3 style="text-align: center;">BALAVICA</h3>\n<p style="text-align: center;">"<strong>Balavica</strong>", a school for special children and also a unit of TNFCT, was established as social initiative to serve the children in need with all kinds of developmental disabilities such as ASD (autistic spectrum disorder), CP, MR, Down Syndrome.</p>\n<p style="text-align: center;">\n</p></div>\n</div></div><div id="panel-91-0-0-1" class="so-panel widget widget_sow-image" data-index="1" data-style="{&quot;background_image_attachment&quot;:false,&quot;background_display&quot;:&quot;tile&quot;}" >[siteorigin_widget class="SiteOrigin_Widget_Image_Widget"]<input type="hidden" value="{&quot;instance&quot;:{&quot;image&quot;:255,&quot;image_fallback&quot;:&quot;&quot;,&quot;size&quot;:&quot;full&quot;,&quot;align&quot;:&quot;default&quot;,&quot;title_align&quot;:&quot;default&quot;,&quot;title&quot;:&quot;&quot;,&quot;title_position&quot;:&quot;hidden&quot;,&quot;alt&quot;:&quot;&quot;,&quot;url&quot;:&quot;&quot;,&quot;bound&quot;:true,&quot;_sow_form_id&quot;:&quot;3388261085bf9d5b0e399a160220923&quot;,&quot;_sow_form_timestamp&quot;:&quot;1543099832385&quot;,&quot;new_window&quot;:false,&quot;full_width&quot;:false,&quot;id&quot;:&quot;sow-image-12510001&quot;,&quot;option_name&quot;:&quot;widget_sow-image&quot;},&quot;args&quot;:{&quot;before_widget&quot;:&quot;&lt;div id=\\&quot;panel-91-0-0-1\\&quot; class=\\&quot;so-