INSERT INTO `wp_posts` VALUES (202, 1, '2018-11-05 10:40:22', '2018-11-05 10:40:22', '<div id="pl-144"  class="panel-layout" ><div id="pg-144-0"  class="panel-grid panel-no-style"  data-style="{&quot;background_image_attachment&quot;:false,&quot;background_display&quot;:&quot;tile&quot;,&quot;cell_alignment&quot;:&quot;flex-start&quot;}"  data-ratio="1"  data-ratio-direction="right" ><div id="pgc-144-0-0"  class="panel-grid-cell"  data-style="{&quot;background_image_attachment&quot;:false,&quot;background_display&quot;:&quot;tile&quot;,&quot;vertical_alignment&quot;:&quot;auto&quot;}"  data-weight="0.5" ><div id="panel-144-0-0-0" class="so-panel widget widget_sow-editor panel-first-child panel-last-child" data-index="0" data-style="{&quot;background_image_attachment&quot;:false,&quot;background_display&quot;:&quot;tile&quot;}" ><div class="so-widget-sow-editor so-widget-sow-editor-base">\n<div class="siteorigin-widget-tinymce textwidget">\n	<h1 style="text-align: center;">We are a Chennai based Young Charitable Trust founded in 2015 by a group of like-minded people who have been living for decades in and around Thirurvalluvar Nagar, Kodungaiyur, Chennai.</h1>\n</div>\n</div></div></div><div id="pgc-144-0-1"  class="panel-grid-cell"  data-weight="0.5" ><div id="panel-144-0-1-0" class="so-panel widget widget_sow-editor panel-first-child panel-last-child" data-index="1" data-style="{&quot;background_image_attachment&quot;:false,&quot;background_display&quot;:&quot;tile&quot;}" ><div class="so-widget-sow-editor so-widget-sow-editor-base">\n<div class="siteorigin-widget-tinymce textwidget">\n	<h2 style="text-align: center;"><strong>WHAT ARE WE DOING?</strong></h2>\n<h3 style="text-align: center;">We have come together and formed this trust with the motto of Empowering the lives of Children and Elderly in whatever way we can. We are aspiring to help the two most vulnerable sections of society, Children and Elderly.</h3>\n<h3 style="text-align: center;">We also pledge to improve the environment by planting trees and educating people about the importance of keeping our surroundings clean.</h3>\n</div>\n</div></div></div></div><div id="pg-144-1"  class="panel-grid panel-no-style" ><div id="pgc-144-1-0"  class="panel-grid-cell"  data-weight="1" ><div id="panel-144-1-0-0" class="so-panel widget widget_sow-editor panel-first-child" data-index="2" data-style="{&quot;background_image_attachment&quot;:false,&quot;background_display&quot;:&quot;tile&quot;}" ><div class="so-widget-sow-editor so-widget-sow-editor-base">\n<div class="siteorigin-widget-tinymce textwidget">\n	<h1 style="text-align: left;">We offer more than supplies – we offer hope</h1>\n<h4 style="text-align: left;">We use donor contributions to make a difference in the lives of children today, tomorrow, and well into the future. We Plant a Better Tomorrow.</h4>\n<ul>\n<li style="text-align: left;">\n<h4>Education</h4>\n</li>\n<li style="text-align: left;">\n<h4>Food</h4>\n</li>\n<li style="text-align: left;">\n<h4>Clothing</h4>\n</li>\n<li style="text-align: left;">\n<h4>Planting of trees</h4>\n</li>\n</ul>\n</div>\n</div></div><div id="panel-144-1-0-1" class="so-panel widget widget_sow-headline panel-last-child" data-index="3" data-style="{&quot;background_image_attachment&quot;:false,&quot;background_display&quot;:&quot;tile&quot;}" >[siteorigin_widget class="SiteOrigin_Widget_Headline_Widget"]<input type="hidden" value="{&quot;instance&quot;:{&quot;headline&quot;:{&quot;text&quot;:&quot;OUR TEAM&quot;,&quot;destination_url&quot;:&quot;&quot;,&quot;tag&quot;:&quot;h1&quot;,&quot;color&quot;:false,&quot;hover_color&quot;:false,&quot;font&quot;:&quot;default&quot;,&quot;font_size&quot;:false,&quot;font_size_unit&quot;:&quot;px&quot;,&quot;align&quot;:&quot;center&quot;,&quot;line_height&quot;:false,&quot;line_height_unit&quot;:&quot;px&quot;,&quot;margin&quot;:false,&quot;margin_unit&quot;:&quot;px&quot;,&quot;so_field_container_state&quot;:&quot;open&quot;,&quot;new_window&quot